import React, { useEffect, useRef } from 'react';

const TableauDashboard = ({ title, description }) => {
  const containerRef = useRef(null);
  const vizId = 'viz1743652535175';

  useEffect(() => {
    // First, clear any existing content in the container
    if (containerRef.current) {
      containerRef.current.innerHTML = '';

      // Create the tableau placeholder div
      const placeholderDiv = document.createElement('div');
      placeholderDiv.className = 'tableauPlaceholder';
      placeholderDiv.id = vizId;
      placeholderDiv.style.position = 'relative';

      // Add the noscript fallback
      const noscript = document.createElement('noscript');
      const noscriptLink = document.createElement('a');
      noscriptLink.href = '#';
      const noscriptImg = document.createElement('img');
      noscriptImg.alt = 'Wild Fire Dashboard Overview';
      noscriptImg.src = 'https://public.tableau.com/static/images/2F/2FTQG3WJG/1_rss.png';
      noscriptImg.style.border = 'none';
      noscriptLink.appendChild(noscriptImg);
      noscript.appendChild(noscriptLink);
      placeholderDiv.appendChild(noscript);

      // Create the viz object element
      const objectEl = document.createElement('object');
      objectEl.className = 'tableauViz';
      objectEl.style.display = 'none';

      // Add all the parameters
      const params = [
        { name: 'host_url', value: 'https%3A%2F%2Fpublic.tableau.com%2F' },
        { name: 'embed_code_version', value: '3' },
        { name: 'site_root', value: '' },
        { name: 'name', value: 'shared/2FTQG3WJG' }, // Updated parameter name and value
        { name: 'tabs', value: 'no' },
        { name: 'toolbar', value: 'yes' },
        { name: 'static_image', value: 'https://public.tableau.com/static/images/2F/2FTQG3WJG/1.png' },
        { name: 'animate_transition', value: 'yes' },
        { name: 'display_static_image', value: 'no' },
        { name: 'display_spinner', value: 'yes' },
        { name: 'display_overlay', value: 'yes' },
        { name: 'display_count', value: 'yes' },
        { name: 'language', value: 'en-US' },
        { name: 'filter', value: 'publish=yes' }
      ];

      params.forEach(param => {
        const paramEl = document.createElement('param');
        paramEl.name = param.name;
        paramEl.value = param.value;
        objectEl.appendChild(paramEl);
      });

      placeholderDiv.appendChild(objectEl);
      containerRef.current.appendChild(placeholderDiv);

      // Add the script
      const scriptElement = document.createElement('script');
      scriptElement.type = 'text/javascript';
      scriptElement.text = `
        var divElement = document.getElementById('${vizId}');
        var vizElement = divElement.getElementsByTagName('object')[0];
        if (divElement.offsetWidth > 800) {
          vizElement.style.width = '1000px';
          vizElement.style.height = '827px';
        } else if (divElement.offsetWidth > 500) {
          vizElement.style.width = '1000px';
          vizElement.style.height = '827px';
        } else {
          vizElement.style.width = '100%';
          vizElement.style.height = '827px';
        }
        var scriptElement = document.createElement('script');
        scriptElement.src = 'https://public.tableau.com/javascripts/api/viz_v1.js';
        vizElement.parentNode.insertBefore(scriptElement, vizElement);
      `;

      containerRef.current.appendChild(scriptElement);
    }

    return () => {
      // Cleanup function
      if (containerRef.current) {
        containerRef.current.innerHTML = '';
      }
    };
  }, []);

  return (
    <div className="chart-container">
      <h3 className="section-title">
        <svg xmlns="http://www.w3.org/2000/svg" className="section-icon" viewBox="0 0 20 20" fill="currentColor">
          <path fillRule="evenodd" d="M2 5a2 2 0 012-2h12a2 2 0 012 2v10a2 2 0 01-2 2H4a2 2 0 01-2-2V5zm3.293 1.293a1 1 0 011.414 0l3 3a1 1 0 010 1.414l-3 3a1 1 0 01-1.414-1.414L7.586 10 5.293 7.707a1 1 0 010-1.414z" clipRule="evenodd" />
        </svg>
        {title || 'Wild Fire Dashboard Overview'}
      </h3>
      <div className="chart-description">
        {description || 'Interactive visualization of California wildfire data using Tableau, providing in-depth analysis and insights.'}
      </div>
      <div className="tableau-container" ref={containerRef} style={{ minHeight: '827px' }}>
        {/* Tableau content will be inserted here by the useEffect */}
      </div>
    </div>
  );
};

export default TableauDashboard;
