import React, { useState } from 'react';
import Dashboard from './components/Dashboard';
import FileUpload from './components/Fileupload';
import './App.css';

const App = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [uploadSuccess, setUploadSuccess] = useState(false);
  const [newDatasetId, setNewDatasetId] = useState(null);

  const handleUploadSuccess = (datasetId) => {
    setUploadSuccess(true);
    setNewDatasetId(datasetId);
    // Auto switch to dashboard tab after successful upload
    setTimeout(() => {
      setActiveTab('dashboard');
    }, 2000);
  };

  return (
    <div className="app-container">
      <nav className="app-nav">
        <div className="app-logo">GeoViz Dashboard</div>
        <div className="app-tabs">
          <button 
            className={`tab-button ${activeTab === 'dashboard' ? 'active' : ''}`}
            onClick={() => setActiveTab('dashboard')}
          >
            Dashboard
          </button>
          <button 
            className={`tab-button ${activeTab === 'upload' ? 'active' : ''}`}
            onClick={() => setActiveTab('upload')}
          >
            Upload Data
          </button>
        </div>
      </nav>

      <main className="app-content">
        {activeTab === 'dashboard' && (
          <Dashboard initialDataset={newDatasetId} />
        )}

        {activeTab === 'upload' && (
          <FileUpload 
            onUploadSuccess={handleUploadSuccess}
            uploadSuccess={uploadSuccess}
            setUploadSuccess={setUploadSuccess}
          />
        )}
      </main>

      <footer className="app-footer">
        <p>Â© 2025 GeoViz Dashboard. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default App;
