// FileUpload.jsx - Component for uploading data files
import React, { useState } from 'react';
import './FileUpload.css';

const FileUpload = ({ onUploadSuccess, uploadSuccess, setUploadSuccess }) => {
  const [files, setFiles] = useState({
    csv: null,
    geojson: null,
    shapefile: []
  });
  const [uploading, setUploading] = useState(false);
  const [error, setError] = useState(null);
  const [datasetName, setDatasetName] = useState('');

  const handleFileChange = (e) => {
    const { name, files: selectedFiles } = e.target;

    if (name === 'shapefile') {
      // Handle multiple shapefile components (.shp, .dbf, .shx)
      const fileArray = Array.from(selectedFiles);
      setFiles(prev => ({
        ...prev,
        [name]: fileArray
      }));
    } else {
      // Handle single file types (CSV, GeoJSON)
      setFiles(prev => ({
        ...prev,
        [name]: selectedFiles[0] || null
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setUploading(true);
    setUploadSuccess(false);

    // Validate that at least one file is selected
    if (!files.csv && !files.geojson && files.shapefile.length === 0) {
      setError('Please select at least one file to upload');
      setUploading(false);
      return;
    }

    // Create form data
    const formData = new FormData();

    if (files.csv) {
      formData.append('csv', files.csv);
    }

    if (files.geojson) {
      formData.append('geojson', files.geojson);
    }

    // Add all shapefile components
    files.shapefile.forEach(file => {
      formData.append('shapefile', file);
    });

    // Add dataset name if provided
    if (datasetName) {
      formData.append('name', datasetName);
    }

    try {
      const response = await fetch('/api/upload', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to upload files');
      }

      // Handle success
      onUploadSuccess(data.datasetId);

      // Reset form
      setFiles({
        csv: null,
        geojson: null,
        shapefile: []
      });
      setDatasetName('');

    } catch (err) {
      setError(err.message);
    } finally {
      setUploading(false);
    }
  };

  const renderFileList = (fileType) => {
    if (fileType === 'shapefile' && files.shapefile.length > 0) {
      return (
        <ul className="file-list">
          {files.shapefile.map((file, index) => (
            <li key={index} className="file-item">
              <span className="file-icon">üìÅ</span>
              <span className="file-name">{file.name}</span>
              <span className="file-size">({formatFileSize(file.size)})</span>
            </li>
          ))}
        </ul>
      );
    } else if (fileType !== 'shapefile' && files[fileType]) {
      const file = files[fileType];
      return (
        <div className="file-item">
          <span className="file-icon">üìÅ</span>
          <span className="file-name">{file.name}</span>
          <span className="file-size">({formatFileSize(file.size)})</span>
        </div>
      );
    }
    return null;
  };

  const formatFileSize = (bytes) => {
    if (bytes < 1024) return bytes + ' B';
    else if (bytes < 1048576) return (bytes / 1024).toFixed(1) + ' KB';
    else return (bytes / 1048576).toFixed(1) + ' MB';
  };

  return (
    <div className="upload-container">
      <h1>Upload Data Files</h1>

      {uploadSuccess ? (
        <div className="success-message">
          <span className="success-icon">‚úì</span>
          <p>Files uploaded successfully! Redirecting to dashboard...</p>
        </div>
      ) : (
        <form onSubmit={handleSubmit} className="upload-form">
          <div className="form-group">
            <label htmlFor="dataset-name">Dataset Name (optional)</label>
            <input
              type="text"
              id="dataset-name"
              value={datasetName}
              onChange={(e) => setDatasetName(e.target.value)}
              placeholder="Enter a name for this dataset"
              className="form-control"
            />
          </div>

          <div className="upload-sections">
            <div className="upload-section">
              <h2>Tabular Data</h2>
              <p className="file-description">
                Upload a CSV file with data to be visualized in charts and tables.
                Make sure it has header rows and consistent data formats.
              </p>

              <div className="file-upload-area">
                <label htmlFor="csv-upload" className="file-upload-label">
                  <div className="upload-icon">üìä</div>
                  <div>Select CSV File</div>
                  <input
                    type="file"
                    id="csv-upload"
                    name="csv"
                    accept=".csv"
                    onChange={handleFileChange}
                    className="file-input"
                  />
                </label>

                {renderFileList('csv')}
              </div>
            </div>

            <div className="upload-section">
              <h2>Geographic Data</h2>
              <p className="file-description">
                Upload GeoJSON or Shapefile with geographic boundaries and properties.
                For Shapefiles, upload .shp, .dbf, and .shx files together.
              </p>

              <div className="file-upload-area">
                <label htmlFor="geojson-upload" className="file-upload-label">
                  <div className="upload-icon">üåç</div>
                  <div>Select GeoJSON File</div>
                  <input
                    type="file"
                    id="geojson-upload"
                    name="geojson"
                    accept=".json,.geojson"
                    onChange={handleFileChange}
                    className="file-input"
                  />
                </label>

                {renderFileList('geojson')}
              </div>

              <div className="file-upload-area">
                <label htmlFor="shapefile-upload" className="file-upload-label">
                  <div className="upload-icon">üó∫Ô∏è</div>
                  <div>Select Shapefile Components</div>
                  <input
                    type="file"
                    id="shapefile-upload"
                    name="shapefile"
                    accept=".shp,.dbf,.shx,.prj"
                    onChange={handleFileChange}
                    className="file-input"
                    multiple
                  />
                </label>

                {renderFileList('shapefile')}
              </div>
            </div>
          </div>

          {error && <div className="error-message">{error}</div>}

          <div className="form-actions">
            <button
              type="submit"
              className="submit-button"
              disabled={uploading}
            >
              {uploading ? 'Uploading...' : 'Upload Files'}
            </button>
          </div>
        </form>
      )}

      <div className="upload-instructions">
        <h3>File Requirements</h3>
        <ul>
          <li>
            <strong>CSV files:</strong> Must have headers in the first row. Numeric columns will be used for calculations.
          </li>
          <li>
            <strong>GeoJSON files:</strong> Must follow the GeoJSON specification with valid features.
          </li>
          <li>
            <strong>Shapefiles:</strong> Upload the .shp, .dbf, and .shx files together.
          </li>
        </ul>
        <p>
          <strong>Note:</strong> For best results, ensure that your CSV data and geographic data 
          share common identifier columns for proper joining of the datasets.
        </p>
      </div>
    </div>
  );
};

export default FileUpload;
