// Dashboard.jsx - Main React component for the dashboard
import React, { useState, useEffect, useRef } from 'react';
import * as d3 from 'd3';
import './Dashboard.css';

const Dashboard = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedDataset, setSelectedDataset] = useState('default');
  const [availableDatasets, setAvailableDatasets] = useState([
    { id: 'default', name: 'Default Dataset' }
  ]);
  const mapRef = useRef(null);
  const chartRef = useRef(null);

  useEffect(() => {
    const fetchDatasets = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/datasets');
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const datasets = await response.json();
        setAvailableDatasets(datasets);
      } catch (err) {
        console.error('Error fetching datasets:', err);
      }
    };

    fetchDatasets();
  }, []);

  useEffect(() => {
    fetchData(selectedDataset);
  }, [selectedDataset]);

  const fetchData = async (dataset) => {
    setLoading(true);
    try {
      const response = await fetch(`http://localhost:8000/api/data/${dataset}`);
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const result = await response.json();
      setData(result);
      setLoading(false);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  useEffect(() => {
    if (data && mapRef.current) {
      drawMap();
    }
    if (data && chartRef.current) {
      drawChart();
    }
  }, [data]);

  const drawMap = () => {
    const mapContainer = d3.select(mapRef.current);
    mapContainer.selectAll("*").remove();

    // Check if we have valid GeoJSON data
    if (!data.geoData || !data.geoData.features || data.geoData.features.length === 0) {
      mapContainer.append("div")
        .attr("class", "no-data-message")
        .text("No geographic data available to display");
      return;
    }

    const width = mapContainer.node().getBoundingClientRect().width;
    const height = 500;

    const svg = mapContainer
      .append("svg")
      .attr("width", width)
      .attr("height", height);

    try {
      const projection = d3.geoMercator()
        .fitSize([width, height], data.geoData);

      const path = d3.geoPath().projection(projection);

      // Find a numeric property to use for coloring
      let valueProperty = 'value';
      if (data.geoData.features.length > 0) {
        const properties = data.geoData.features[0].properties;
        const numericProps = Object.keys(properties).filter(key => 
          typeof properties[key] === 'number' || 
          (typeof properties[key] === 'string' && !isNaN(parseFloat(properties[key])))
        );

        if (numericProps.length > 0 && !properties.hasOwnProperty('value')) {
          valueProperty = numericProps[0];
        }
      }

      // Get min/max values for color scale
      const values = data.geoData.features.map(f => {
        const val = f.properties[valueProperty];
        return typeof val === 'number' ? val : parseFloat(val);
      }).filter(val => !isNaN(val));

      const minValue = values.length > 0 ? Math.min(...values) : 0;
      const maxValue = values.length > 0 ? Math.max(...values) : 1;

      // Create a color scale based on the property
      const colorScale = d3.scaleSequential()
        .domain([minValue, maxValue])
        .interpolator(d3.interpolateViridis);

      // Draw map
      svg.selectAll("path")
        .data(data.geoData.features)
        .enter()
        .append("path")
        .attr("d", path)
        .attr("fill", d => {
          const val = d.properties[valueProperty];
          const numericVal = typeof val === 'number' ? val : parseFloat(val);
          return !isNaN(numericVal) ? colorScale(numericVal) : "#ccc";
        })
        .attr("stroke", "#fff")
        .attr("stroke-width", 0.5)
        .on("mouseover", function(event, d) {
          d3.select(this)
            .attr("stroke-width", 1.5)
            .attr("stroke", "#000");

          // Show tooltip with properties
          const tooltip = d3.select(".tooltip");
          let tooltipHTML = `<div style="font-weight:bold;border-bottom:1px solid #ccc;padding-bottom:3px;margin-bottom:3px">
            ${d.properties.name || d.properties.NAME || 'Feature'}</div>`;

          // Add all properties to tooltip
          Object.entries(d.properties).forEach(([key, value]) => {
            if (key !== 'name' && key !== 'NAME') {
              tooltipHTML += `<div><strong>${key}:</strong> ${value}</div>`;
            }
          });

          tooltip
            .style("left", (event.pageX + 10) + "px")
            .style("top", (event.pageY - 20) + "px")
            .style("display", "block")
            .html(tooltipHTML);
        })
        .on("mouseout", function() {
          d3.select(this)
            .attr("stroke-width", 0.5)
            .attr("stroke", "#fff");

          // Hide tooltip
          d3.select(".tooltip").style("display", "none");
        });

      // Add legend
      const legendWidth = 20;
      const legendHeight = 200;
      const legendX = width - legendWidth - 20;
      const legendY = (height - legendHeight) / 2;

      const legend = svg.append("g")
        .attr("transform", `translate(${legendX}, ${legendY})`);

      const legendScale = d3.scaleLinear()
        .domain(colorScale.domain())
        .range([legendHeight, 0]);

      const legendAxis = d3.axisRight(legendScale)
        .tickSize(legendWidth)
        .ticks(5);

      legend.append("g")
        .call(legendAxis);

      const defs = svg.append("defs");
      const linearGradient = defs.append("linearGradient")
        .attr("id", "linear-gradient")
        .attr("x1", "0%")
        .attr("y1", "100%")
        .attr("x2", "0%")
        .attr("y2", "0%");

      const stops = [0, 0.2, 0.4, 0.6, 0.8, 1];
      stops.forEach(stop => {
        linearGradient.append("stop")
          .attr("offset", `${stop * 100}%`)
          .attr("stop-color", colorScale(minValue + stop * (maxValue - minValue)));
      });

      legend.append("rect")
        .attr("width", legendWidth)
        .attr("height", legendHeight)
        .style("fill", "url(#linear-gradient)");

      // Add legend title
      legend.append("text")
        .attr("x", -10)
        .attr("y", -10)
        .attr("text-anchor", "end")
        .style("font-size", "12px")
        .text(valueProperty);
    } catch (err) {
      console.error('Error rendering map:', err);
      svg.append("text")
        .attr("x", width / 2)
        .attr("y", height / 2)
        .attr("text-anchor", "middle")
        .text("Error rendering map. See console for details.");
    }
  };

  const drawChart = () => {
    const chartContainer = d3.select(chartRef.current);
    chartContainer.selectAll("*").remove();

    if (!data.csvData || data.csvData.length === 0) {
      chartContainer.append("div")
        .attr("class", "no-data-message")
        .text("No chart data available to display");
      return;
    }

    const width = chartContainer.node().getBoundingClientRect().width;
    const height = 300;
    const margin = { top: 20, right: 30, bottom: 40, left: 50 };
    const innerWidth = width - margin.left - margin.right;
    const innerHeight = height - margin.top - margin.bottom;

    const svg = chartContainer
      .append("svg")
      .attr("width", width)
      .attr("height", height);

    const g = svg
      .append("g")
      .attr("transform", `translate(${margin.left}, ${margin.top})`);

    // Process data for bar chart
    const chartData = data.csvData.slice(0, 10); // Limit to 10 items for clarity

    const x = d3.scaleBand()
      .domain(chartData.map(d => d.category))
      .range([0, innerWidth])
      .padding(0.1);

    const y = d3.scaleLinear()
      .domain([0, d3.max(chartData, d => d.value)])
      .nice()
      .range([innerHeight, 0]);

    // Add X axis
    g.append("g")
      .attr("transform", `translate(0, ${innerHeight})`)
      .call(d3.axisBottom(x))
      .selectAll("text")
      .attr("y", 10)
      .attr("x", -5)
      .attr("dy", ".35em")
      .attr("transform", "rotate(-45)")
      .style("text-anchor", "end");

    // Add Y axis
    g.append("g")
      .call(d3.axisLeft(y));

    // Add bars
    g.selectAll(".bar")
      .data(chartData)
      .enter()
      .append("rect")
      .attr("class", "bar")
      .attr("x", d => x(d.category))
      .attr("y", d => y(d.value))
      .attr("width", x.bandwidth())
      .attr("height", d => innerHeight - y(d.value))
      .attr("fill", "#4682b4")
      .on("mouseover", function(event, d) {
        d3.select(this).attr("fill", "#1e5180");

        // Show tooltip
        const tooltip = d3.select(".tooltip");
        tooltip
          .style("left", (event.pageX + 10) + "px")
          .style("top", (event.pageY - 20) + "px")
          .style("display", "block")
          .html(`
            <div><strong>${d.category}</strong></div>
            <div>Value: ${d.value}</div>
          `);
      })
      .on("mouseout", function() {
        d3.select(this).attr("fill", "#4682b4");
        d3.select(".tooltip").style("display", "none");
      });

    // Add title
    svg.append("text")
      .attr("x", width / 2)
      .attr("y", margin.top / 2)
      .attr("text-anchor", "middle")
      .style("font-size", "16px")
      .text("Top Categories by Value");
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>Data Visualization Dashboard</h1>
        <div className="controls">
          <label htmlFor="dataset-select">Select Dataset:</label>
          <select 
            id="dataset-select" 
            value={selectedDataset}
            onChange={(e) => setSelectedDataset(e.target.value)}
          >
            {availableDatasets.map(dataset => (
              <option key={dataset.id} value={dataset.id}>
                {dataset.name}
              </option>
            ))}
          </select>
        </div>
      </header>

      {loading && <div className="loading">Loading data...</div>}
      {error && <div className="error">Error: {error}</div>}

      {data && !loading && (
        <div className="dashboard-content">
          <div className="widget full-width">
            <h2>Geographic Data Visualization</h2>
            <div className="map-container" ref={mapRef}></div>
          </div>

          <div className="widget-row">
            <div className="widget">
              <h2>Data Trends</h2>
              <div className="chart-container" ref={chartRef}></div>
            </div>

            <div className="widget">
              <h2>Key Metrics</h2>
              <div className="metrics-container">
                <div className="metric-card">
                  <h3>Total</h3>
                  <p className="metric-value">{data.metrics?.total || 'N/A'}</p>
                </div>
                <div className="metric-card">
                  <h3>Average</h3>
                  <p className="metric-value">{data.metrics?.average?.toFixed(2) || 'N/A'}</p>
                </div>
                <div className="metric-card">
                  <h3>Maximum</h3>
                  <p className="metric-value">{data.metrics?.maximum || 'N/A'}</p>
                </div>
                <div className="metric-card">
                  <h3>Minimum</h3>
                  <p className="metric-value">{data.metrics?.minimum || 'N/A'}</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      <div className="tooltip" style={{ display: 'none' }}></div>
    </div>
  );
};

export default Dashboard;
