import React, { useEffect, useRef } from 'react';

const TableauDashboard = ({ title, description }) => {
  const vizRef = useRef(null);

  useEffect(() => {
    // Load the Tableau JS API
    const loadTableauViz = () => {
      const script = document.createElement('script');
      script.src = 'https://public.tableau.com/javascripts/api/tableau-2.min.js';
      script.async = true;

      script.onload = () => {
        initializeTableau();
      };

      document.body.appendChild(script);
      return script;
    };

    const initializeTableau = () => {
      if (window.tableau && vizRef.current) {
        // Clear any existing content
        while (vizRef.current.firstChild) {
          vizRef.current.removeChild(vizRef.current.firstChild);
        }

        // Create new visualization
        const vizUrl = 'https://public.tableau.com/views/shared/KKG9DDH7G';
        try {
          new window.tableau.Viz(vizRef.current, vizUrl, {
            hideTabs: false,
            hideToolbar: false,
            width: '100%',
            height: '827px'
          });
        } catch (err) {
          console.error('Failed to initialize Tableau viz:', err);
        }
      }
    };

    const script = loadTableauViz();

    return () => {
      // Cleanup
      if (document.body.contains(script)) {
        document.body.removeChild(script);
      }
    };
  }, []);

  return (
    <div className="chart-container">
      <h3 className="section-title">
        <svg xmlns="http://www.w3.org/2000/svg" className="section-icon" viewBox="0 0 20 20" fill="currentColor">
          <path fillRule="evenodd" d="M2 5a2 2 0 012-2h12a2 2 0 012 2v10a2 2 0 01-2 2H4a2 2 0 01-2-2V5zm3.293 1.293a1 1 0 011.414 0l3 3a1 1 0 010 1.414l-3 3a1 1 0 01-1.414-1.414L7.586 10 5.293 7.707a1 1 0 010-1.414z" clipRule="evenodd" />
        </svg>
        {title || 'Wild Fire Dashboard Overview'}
      </h3>
      <div className="chart-description">
        {description || 'Interactive visualization of California wildfire data using Tableau, providing in-depth analysis and insights.'}
      </div>
      <div className="tableau-container" style={{ minHeight: '827px' }}>
        <div ref={vizRef} style={{ width: '100%', height: '827px' }}></div>
      </div>
    </div>
  );
};

export default TableauDashboard;
