import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

export const RadialFireChart = ({ 
  yearlyData, 
  monthlyDataByYear,
  availableYears,
  selectedMetric = 'fires' // 'fires' or 'acres'
}) => {
  const radialChartRef = useRef(null);

  useEffect(() => {
    if (monthlyDataByYear && Object.keys(monthlyDataByYear).length > 0) {
      createRadialChart();
    }
  }, [monthlyDataByYear, selectedMetric, availableYears]);

  const formatLargeNumber = (num) => {
    if (num >= 1000000) {
      return `${(num / 1000000).toFixed(1)}M`;
    } else if (num >= 1000) {
      return `${(num / 1000).toFixed(1)}K`;
    }
    return num;
  };

  const createRadialChart = () => {
    if (!radialChartRef.current) return;

    // Clear previous chart
    d3.select(radialChartRef.current).selectAll("*").remove();

    // Prepare data for the radial chart
    const processedData = [];
    const months = [
      'January', 'February', 'March', 'April', 'May', 'June',
      'July', 'August', 'September', 'October', 'November', 'December'
    ];

    // Get the last 5 years or all years if less than 5
    const sortedYears = [...availableYears].sort((a, b) => b - a);
    const yearsToShow = sortedYears.slice(0, 5);

    // Create data structure for the radial chart
    yearsToShow.forEach(year => {
      if (monthlyDataByYear[year]) {
        months.forEach((month, i) => {
          const monthData = monthlyDataByYear[year].find(m => m.month === month);
          if (monthData) {
            processedData.push({
              year: year,
              month: month,
              monthIndex: i,
              angle: (i / 12) * 2 * Math.PI, // position in radians
              value: selectedMetric === 'fires' ? monthData.fires : monthData.acres
            });
          }
        });
      }
    });

    // Set dimensions and margins
    const margin = { top: 50, right: 50, bottom: 50, left: 50 };
    const width = radialChartRef.current.clientWidth || 600;
    const height = width; // Make it square for better radial visualization
    const innerRadius = 80;
    const outerRadius = Math.min(width, height) / 2 - margin.top;

    // Create SVG
    const svg = d3.select(radialChartRef.current)
      .attr("width", width)
      .attr("height", height)
      .append("g")
      .attr("transform", `translate(${width / 2}, ${height / 2})`);

    // Define scales
    // Angle scale for months
    const angleScale = d3.scaleLinear()
      .domain([0, 12])
      .range([0, 2 * Math.PI]);

    // Radius scale for values
    const maxValue = d3.max(processedData, d => d.value);
    const radiusScale = d3.scaleLinear()
      .domain([0, maxValue])
      .range([innerRadius, outerRadius]);

    // Year scale for color
    const colorScale = d3.scaleOrdinal()
      .domain(yearsToShow)
      .range(d3.schemeCategory10);

    // Create month axis (circles and lines)
    const monthCircles = svg.append("g")
      .attr("class", "month-circles");

    // Add circles for each month
    monthCircles.selectAll("circle")
      .data(d3.range(1, 6)) // Create 5 concentric circles
      .enter()
      .append("circle")
      .attr("r", d => innerRadius + (d * (outerRadius - innerRadius) / 5))
      .attr("fill", "none")
      .attr("stroke", "#e5e7eb")
      .attr("stroke-dasharray", "2,2")
      .attr("stroke-width", 0.5);

    // Add value labels to circles
    monthCircles.selectAll(".radius-label")
      .data(d3.range(1, 6))
      .enter()
      .append("text")
      .attr("class", "radius-label")
      .attr("y", d => -(innerRadius + (d * (outerRadius - innerRadius) / 5)))
      .attr("dy", "0.35em")
      .attr("text-anchor", "middle")
      .attr("font-size", "10px")
      .attr("fill", "#6b7280")
      .text(d => {
        const value = maxValue * (d / 5);
        return formatLargeNumber(value);
      });

    // Add axis lines for months
    const monthLines = svg.append("g")
      .attr("class", "month-lines");

    monthLines.selectAll("line")
      .data(months)
      .enter()
      .append("line")
      .attr("x1", 0)
      .attr("y1", 0)
      .attr("x2", (d, i) => Math.cos(angleScale(i) - Math.PI / 2) * outerRadius)
      .attr("y2", (d, i) => Math.sin(angleScale(i) - Math.PI / 2) * outerRadius)
      .attr("stroke", "#e5e7eb")
      .attr("stroke-width", 0.5);

    // Add month labels
    svg.selectAll(".month-label")
      .data(months)
      .enter()
      .append("text")
      .attr("class", "month-label")
      .attr("x", (d, i) => Math.cos(angleScale(i) - Math.PI / 2) * (outerRadius + 20))
      .attr("y", (d, i) => Math.sin(angleScale(i) - Math.PI / 2) * (outerRadius + 20))
      .attr("dy", "0.35em")
      .attr("text-anchor", (d, i) => {
        const angle = angleScale(i);
        if (angle === 0 || angle === Math.PI) return "middle";
        return angle < Math.PI ? "start" : "end";
      })
      .attr("font-size", "12px")
      .attr("fill", "#4b5563")
      .text(d => d.substring(0, 3)); // Just show first 3 letters

    // Group data by year for line generation
    const dataByYear = d3.group(processedData, d => d.year);

    // Create a line generator for each year
    const lineGenerator = d3.lineRadial()
      .angle(d => angleScale(d.monthIndex) - Math.PI / 2)
      .radius(d => radiusScale(d.value))
      .curve(d3.curveCardinalClosed);

    // Draw lines for each year
    const yearLines = svg.append("g")
      .attr("class", "year-lines");

    dataByYear.forEach((yearData, year) => {
      // Sort by month index for proper connection
      yearData.sort((a, b) => a.monthIndex - b.monthIndex);

      // Add the path (line)
      yearLines
        .append("path")
        .datum(yearData)
        .attr("d", lineGenerator)
        .attr("fill", colorScale(year))
        .attr("fill-opacity", 0.2)
        .attr("stroke", colorScale(year))
        .attr("stroke-width", 2.5)
        .attr("stroke-linejoin", "round");

      // Add dots for each month's data point
      yearLines
        .selectAll(`circle-year-${year}`)
        .data(yearData)
        .enter()
        .append("circle")
        .attr("cx", d => Math.cos(angleScale(d.monthIndex) - Math.PI / 2) * radiusScale(d.value))
        .attr("cy", d => Math.sin(angleScale(d.monthIndex) - Math.PI / 2) * radiusScale(d.value))
        .attr("r", 4)
        .attr("fill", colorScale(year))
        .attr("stroke", "#fff")
        .attr("stroke-width", 1.5);
    });

    // Add central label
    svg.append("text")
      .attr("text-anchor", "middle")
      .attr("dy", "0.35em")
      .attr("font-size", "14px")
      .attr("font-weight", "bold")
      .text(selectedMetric === 'fires' ? "Fire Count" : "Acres Burned");

    // Add legend
    const legend = svg.append("g")
      .attr("class", "legend")
      .attr("transform", `translate(${-width/2 + 20}, ${-height/2 + 20})`);

    yearsToShow.forEach((year, i) => {
      const legendItem = legend.append("g")
        .attr("transform", `translate(0, ${i * 20})`);

      legendItem.append("rect")
        .attr("width", 15)
        .attr("height", 15)
        .attr("fill", colorScale(year));

      legendItem.append("text")
        .attr("x", 20)
        .attr("y", 12)
        .text(year)
        .attr("font-size", "12px");
    });

    // Add title
    svg.append("text")
      .attr("x", 0)
      .attr("y", -height/2 + 20)
      .attr("text-anchor", "middle")
      .attr("font-size", "16px")
      .attr("font-weight", "bold")
      .text(`Monthly ${selectedMetric === 'fires' ? 'Fire Counts' : 'Acres Burned'} by Year`);

    // Create tooltip
    const tooltip = d3.select("body")
      .selectAll(".tooltip")
      .data([null])
      .join("div")
      .attr("class", "tooltip")
      .style("opacity", 0);

    // Add hover effects for dots
    svg.selectAll("circle")
      .on("mouseover", function(event, d) {
        if (!d || !d.value) return; // Skip if not a data point

        d3.select(this)
          .transition()
          .duration(200)
          .attr("r", 6);

        tooltip.transition()
          .duration(200)
          .style("opacity", 0.9);

        tooltip.html(`
          <strong>${d.month} ${d.year}</strong><br/>
          ${selectedMetric === 'fires' ? 'Fires' : 'Acres Burned'}: ${d.value.toLocaleString()}
        `)
          .style("left", (event.pageX + 10) + "px")
          .style("top", (event.pageY - 28) + "px");
      })
      .on("mouseout", function(event, d) {
        if (!d || !d.value) return; // Skip if not a data point

        d3.select(this)
          .transition()
          .duration(200)
          .attr("r", 4);

        tooltip.transition()
          .duration(500)
          .style("opacity", 0);
      });
  };

  return (
    <div className="chart-container">
      <h3 className="section-title">
        <svg xmlns="http://www.w3.org/2000/svg" className="section-icon" viewBox="0 0 20 20" fill="currentColor">
          <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-12a1 1 0 10-2 0v4a1 1 0 00.293.707l2.828 2.829a1 1 0 101.415-1.415L11 9.586V6z" clipRule="evenodd" />
        </svg>
        Seasonal Fire Patterns (Radial View)
      </h3>
      <div className="chart-description">
        This radial chart shows the seasonal pattern of {selectedMetric === 'fires' ? 'fire occurrences' : 'acres burned'} 
        across multiple years. Each circle represents a year, with months positioned clockwise around the circle.
        Compare annual cycles and identify recurring seasonal patterns.
      </div>
      <div className="chart-canvas" style={{ display: 'flex', justifyContent: 'center' }}>
        <svg ref={radialChartRef} width="100%" height="600"></svg>
      </div>
    </div>
  );
};

export default RadialFireChart;
