import React, { useEffect, useRef } from 'react';

const TableauDashboard = ({ url, title, description, height = 800 }) => {
  const containerRef = useRef(null);

  useEffect(() => {
    // Add a small delay to ensure the container is fully rendered
    const timer = setTimeout(() => {
      // Adjust iframe height based on container width to maintain aspect ratio
      if (containerRef.current) {
        const width = containerRef.current.offsetWidth;
        const iframeElement = containerRef.current.querySelector('iframe');
        if (iframeElement) {
          // You can adjust this calculation based on your dashboard's aspect ratio
          iframeElement.style.height = `${height}px`;
        }
      }
    }, 100);

    return () => clearTimeout(timer);
  }, [height]);

  return (
    <div className="chart-container">
      <h3 className="section-title">
        <svg xmlns="http://www.w3.org/2000/svg" className="section-icon" viewBox="0 0 20 20" fill="currentColor">
          <path fillRule="evenodd" d="M2 5a2 2 0 012-2h12a2 2 0 012 2v10a2 2 0 01-2 2H4a2 2 0 01-2-2V5zm3.293 1.293a1 1 0 011.414 0l3 3a1 1 0 010 1.414l-3 3a1 1 0 01-1.414-1.414L7.586 10 5.293 7.707a1 1 0 010-1.414z" clipRule="evenodd" />
        </svg>
        {title}
      </h3>
      <div className="chart-description">
        {description}
      </div>
      <div className="tableau-container" ref={containerRef}>
        <iframe
          src={url}
          frameBorder="0"
          allowFullScreen
          title={title}
          width="100%"
          height={height}
        ></iframe>
      </div>
    </div>
  );
};

export default TableauDashboard;
