import React, { useState, useEffect } from 'react';
import { BarChart, Bar, LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import './CaliforniaFireDashboard.css';

const CaliforniaFireDashboard = () => {
  const [yearlyData, setYearlyData] = useState([]);
  const [monthlyData, setMonthlyData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedYear, setSelectedYear] = useState(null);
  const [availableYears, setAvailableYears] = useState([]);
  const [summaryStats, setSummaryStats] = useState({
    totalFires: 0,
    totalAcres: 0,
    yearlyAcres: 0,
    peakMonth: '',
    worstYear: '',
    worstYearAcres: 0
  });

  const backendBaseUrl = 'http://localhost:8000';

  // Load yearly data when component mounts
  useEffect(() => {
    fetchYearlyData();
  }, []);

  // Fetch monthly data when selected year changes
  useEffect(() => {
    if (selectedYear) {
      fetchMonthlyData(selectedYear);
    }
  }, [selectedYear]);

  const fetchYearlyData = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`${backendBaseUrl}/api/stats/yearly?dataset=firep23_1`);

      if (!response.ok) {
        // If we get a 404, it means the statistics file isn't available
        if (response.status === 404) {
          console.warn("Statistics file not found");
          handleDataError("Statistics file not found. Please run the preprocessor script first.");
          return;
        }
        throw new Error(`Failed to fetch yearly data: ${response.statusText}`);
      }

      const data = await response.json();

      setYearlyData(data.yearlyData);
      setAvailableYears(data.years);

      // Update summary stats
      setSummaryStats(prev => ({
        ...prev,
        totalFires: data.summary.totalFires,
        totalAcres: data.summary.totalAcres,
        worstYear: data.summary.worstYear,
        worstYearAcres: data.summary.worstYearAcres
      }));

      // Set the most recent year as the default selected year
      if (data.years.length > 0) {
        const maxYear = Math.max(...data.years.map(y => parseInt(y)));
        setSelectedYear(maxYear.toString());
      }

    } catch (err) {
      console.error("Error fetching yearly fire data:", err);
      handleDataError("Failed to load fire data. Please check server connection and run the preprocessor script.");
    } finally {
      setLoading(false);
    }
  };

  const fetchMonthlyData = async (year) => {
    if (error || !year) {
      setEmptyMonthlyData();
      return;
    }

    try {
      // Only show loading indicator for initial data load, not for year changes
      if (monthlyData.length === 0) {
        setLoading(true);
      }

      const response = await fetch(`${backendBaseUrl}/api/stats/monthly?dataset=firep23_1&year=${year}`);
      if (!response.ok) {
        if (response.status === 404) {
          console.warn("Monthly statistics not found");
          setError("Monthly statistics for this year not found. Please run the preprocessor script first.");
          setEmptyMonthlyData();
          return;
        }
        throw new Error(`Failed to fetch monthly data: ${response.statusText}`);
      }

      const data = await response.json();

      setMonthlyData(data.monthlyData);

      // Update summary stats for the selected year
      setSummaryStats(prev => ({
        ...prev,
        yearlyAcres: data.summary.totalAcres,
        peakMonth: data.summary.peakMonth
      }));

    } catch (err) {
      console.error(`Error fetching monthly fire data for year ${year}:`, err);
      setError(`Failed to load monthly data for ${year}. Please check server connection.`);
      setEmptyMonthlyData();
    } finally {
      setLoading(false);
    }
  };

  // Handle data loading error
  const handleDataError = (errorMessage) => {
    setYearlyData([]);
    setAvailableYears([]);
    setSelectedYear(null);
    setError(errorMessage || "Failed to load fire data. Please run the preprocessor script first.");
  };

  // Set empty monthly data when error occurs
  const setEmptyMonthlyData = () => {
    setMonthlyData([]);
    setSummaryStats(prev => ({
      ...prev,
      yearlyAcres: 0,
      peakMonth: 'N/A'
    }));
  };

  const handleYearChange = (e) => {
    setSelectedYear(e.target.value);
  };

  const formatLargeNumber = (num) => {
    if (num >= 1000000) {
      return `${(num / 1000000).toFixed(1)}M`;
    } else if (num >= 1000) {
      return `${(num / 1000).toFixed(1)}K`;
    }
    return num;
  };

  if (loading && yearlyData.length === 0) return (
    <div className="loading-container">
      <div className="loading-spinner">
        <div className="spinner"></div>
        <div className="loading-text">Loading fire data...</div>
      </div>
    </div>
  );

  if (error) return (
    <div className="error-container">
      <div className="dashboard-header">
        <h2 className="dashboard-title">California Wildfire Dashboard</h2>
      </div>

      <div className="error-message">
        <div className="flex">
          <div className="flex-shrink-0">
            <svg className="error-icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
            </svg>
          </div>
          <div className="ml-3">
            <h3 className="error-title">Data Loading Error</h3>
            <div className="error-details">
              <p>{error}</p>
            </div>
            <div>
              <p className="error-help">To fix this issue:</p>
              <ol className="error-list">
                <li className="error-list-item">Make sure the server is running at {backendBaseUrl}</li>
                <li className="error-list-item">Ensure your GeoJSON files are placed in the 'uploads' directory</li>
                <li className="error-list-item">Run the preprocessor script to generate statistics files:</li>
              </ol>
              <div className="error-command">
                <code>node preprocessor.js</code>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="text-center">
        <button 
          onClick={fetchYearlyData} 
          className="error-retry-button"
        >
          Try Again
        </button>
      </div>
    </div>
  );

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h2 className="dashboard-title">California Wildfire Dashboard</h2>
        <p className="dashboard-description">
          Visualization of wildfire data from California showing yearly trends and monthly breakdown.
        </p>
      </div>

      {/* Year selector */}
      <div className="year-selector">
        <label htmlFor="year-select" className="year-selector-label">Select Year for Monthly Data:</label>
        <select
          id="year-select"
          value={selectedYear || ''}
          onChange={handleYearChange}
          className="year-selector-dropdown"
          disabled={availableYears.length === 0}
        >
          {availableYears.map(year => (
            <option key={year} value={year}>{year}</option>
          ))}
        </select>
      </div>

      {/* Yearly Fire Chart */}
      <div className="chart-container">
        <h3 className="chart-title">
          <svg xmlns="http://www.w3.org/2000/svg" className="chart-title-icon blue" viewBox="0 0 20 20" fill="currentColor">
            <path d="M2 10a8 8 0 018-8v8h8a8 8 0 11-16 0z" />
            <path d="M12 2.252A8.014 8.014 0 0117.748 8H12V2.252z" />
          </svg>
          Wildfires over the years
        </h3>
        <div className="chart-canvas">
          <ResponsiveContainer width="100%" height="100%">
            <LineChart
              data={yearlyData}
              margin={{ top: 10, right: 30, left: 20, bottom: 30 }}
            >
              <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="#E5E7EB" />
              <XAxis 
                dataKey="year"
                tick={{ fontSize: 12 }}
                tickLine={false}
                axisLine={{ stroke: '#E5E7EB' }}
              />
              <YAxis 
                tick={{ fontSize: 12 }}
                tickLine={false}
                axisLine={{ stroke: '#E5E7EB' }}
                domain={[0, 'dataMax + 100']}
              />
              <Tooltip
                formatter={(value) => [value, 'Count of Fires']}
                labelFormatter={(label) => `Year: ${label}`}
              />
              <Line 
                type="monotone" 
                dataKey="fires" 
                stroke="#2563EB" 
                strokeWidth={2.5}
                dot={{ fill: '#2563EB', r: 4, strokeWidth: 1, stroke: '#fff' }}
                activeDot={{ r: 6, stroke: '#2563EB', strokeWidth: 2 }}
                name="Fire Count"
              />
              {/* Add trend line */}
              <Line 
                type="linear" 
                dataKey="fires" 
                stroke="#F97316" 
                strokeWidth={2}
                strokeDasharray="5 5"
                activeDot={false}
                dot={false}
                connectNulls={true}
                isAnimationActive={false}
                legendType="none"
                name="Trend"
              />
              <Legend verticalAlign="top" height={36} />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Monthly Acres Burned Chart */}
      <div className="chart-container">
        <h3 className="chart-title">
          <svg xmlns="http://www.w3.org/2000/svg" className="chart-title-icon red" viewBox="0 0 20 20" fill="currentColor">
            <path fillRule="evenodd" d="M5 3a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2V5a2 2 0 00-2-2H5zm9 4a1 1 0 10-2 0v6a1 1 0 102 0V7zm-3 2a1 1 0 10-2 0v4a1 1 0 102 0V9zm-3 3a1 1 0 10-2 0v1a1 1 0 102 0v-1z" clipRule="evenodd" />
          </svg>
          Acres burned by month in {selectedYear || '...'}
        </h3>
        <div className="chart-canvas">
          <ResponsiveContainer width="100%" height="100%">
            <BarChart
              data={monthlyData}
              margin={{ top: 20, right: 30, left: 20, bottom: 60 }}
            >
              <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="#E5E7EB" />
              <XAxis 
                dataKey="month" 
                tick={{ fontSize: 12 }}
                tickLine={false}
                axisLine={{ stroke: '#E5E7EB' }}
                interval={0}
                angle={-45}
                textAnchor="end"
                height={60}
              />
              <YAxis 
                tick={{ fontSize: 12 }}
                tickLine={false}
                axisLine={{ stroke: '#E5E7EB' }}
                tickFormatter={formatLargeNumber}
              />
              <Tooltip
                formatter={(value) => [new Intl.NumberFormat().format(value), 'Acres Burned']}
                labelFormatter={(label) => `Month: ${label}`}
              />
              <Bar 
                dataKey="acres" 
                fill="#DC2626" 
                name="Acres Burned" 
                barSize={30}
                radius={[4, 4, 0, 0]}
                label={{
                  position: 'top',
                  formatter: (value) => value > 10000 ? formatLargeNumber(value) : '',
                  fontSize: 12,
                  fill: '#6B7280'
                }}
              />
              <Legend verticalAlign="top" height={36} />
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Summary Stats */}
      <div className="stats-grid">
        <div className="stat-card stat-card-blue">
          <h3 className="stat-card-title blue">
            <svg xmlns="http://www.w3.org/2000/svg" className="stat-card-icon" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M12.395 2.553a1 1 0 00-1.45-.385c-.345.23-.614.558-.822.88-.214.33-.403.713-.57 1.116-.334.804-.614 1.768-.84 2.734a31.365 31.365 0 00-.613 3.58 2.64 2.64 0 01-.945-1.067c-.328-.68-.398-1.534-.398-2.654A1 1 0 005.05 6.05 6.981 6.981 0 003 11a7 7 0 1011.95-4.95c-.592-.591-.98-.985-1.348-1.467-.363-.476-.724-1.063-1.207-2.03zM12.12 15.12A3 3 0 017 13s.879.5 2.5.5c0-1 .5-4 1.25-4.5.5 1 .786 1.293 1.371 1.879A2.99 2.99 0 0113 13a2.99 2.99 0 01-.879 2.121z" clipRule="evenodd" />
            </svg>
            Total Fires
          </h3>
          <p className="stat-card-value blue">{summaryStats.totalFires.toLocaleString()}</p>
        </div>

        <div className="stat-card stat-card-orange">
          <h3 className="stat-card-title orange">
            <svg xmlns="http://www.w3.org/2000/svg" className="stat-card-icon" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M5 2a2 2 0 00-2 2v14l3.5-2 3.5 2 3.5-2 3.5 2V4a2 2 0 00-2-2H5zm4.707 3.707a1 1 0 00-1.414-1.414l-3 3a1 1 0 000 1.414l3 3a1 1 0 001.414-1.414L8.414 9H10a3 3 0 013 3v1a1 1 0 102 0v-1a5 5 0 00-5-5H8.414l1.293-1.293z" clipRule="evenodd" />
            </svg>
            Total Acres (All Years)
          </h3>
          <p className="stat-card-value orange">{summaryStats.totalAcres.toLocaleString()}</p>
        </div>

        <div className="stat-card stat-card-red">
          <h3 className="stat-card-title red">
            <svg xmlns="http://www.w3.org/2000/svg" className="stat-card-icon" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M5 2a1 1 0 011 1v1h1a1 1 0 010 2H6v1a1 1 0 01-2 0V6H3a1 1 0 010-2h1V3a1 1 0 011-1zm0 10a1 1 0 011 1v1h1a1 1 0 110 2H6v1a1 1 0 11-2 0v-1H3a1 1 0 110-2h1v-1a1 1 0 011-1zm7-10a1 1 0 01.707.293l.707.707L15 4.414A2 2 0 0116.414 6L14 8.414l.707.707a1 1 0 01-1.414 1.414L12 9.414l-1.293 1.293a1 1 0 01-1.414-1.414L10.586 8 8.414 5.82A1 1 0 018.414 4.4L10.586 2.2A1 1 0 0112 2z" clipRule="evenodd" />
            </svg>
            Total Acres in {selectedYear || '...'}
          </h3>
          <p className="stat-card-value red">{summaryStats.yearlyAcres.toLocaleString()}</p>
        </div>

        <div className="stat-card stat-card-amber">
          <h3 className="stat-card-title amber">
            <svg xmlns="http://www.w3.org/2000/svg" className="stat-card-icon" viewBox="0 0 20 20" fill="currentColor">
              <path d="M5 4a1 1 0 00-2 0v7.268a2 2 0 000 3.464V16a1 1 0 102 0v-1.268a2 2 0 000-3.464V4zM11 4a1 1 0 10-2 0v1.268a2 2 0 000 3.464V16a1 1 0 102 0V8.732a2 2 0 000-3.464V4zM16 3a1 1 0 011 1v7.268a2 2 0 010 3.464V16a1 1 0 11-2 0v-1.268a2 2 0 010-3.464V4a1 1 0 011-1z" />
            </svg>
            Peak Month in {selectedYear || '...'}
          </h3>
          <p className="stat-card-value amber">{summaryStats.peakMonth || 'N/A'}</p>
        </div>
      </div>

      {/* Data Source Info */}
      <div className="data-source-container">
        <h3 className="data-source-title">
          <svg xmlns="http://www.w3.org/2000/svg" className="data-source-icon" viewBox="0 0 20 20" fill="currentColor">
            <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2v-3a1 1 0 00-1-1H9z" clipRule="evenodd" />
          </svg>
          Data Source Information
        </h3>
        <div className="data-source-content">
          <p>
            This visualization uses data from the firep23_1.geojson file, which contains California wildfire records.
            The yearly chart displays both the count of fire incidents and total acres burned per year (based on the YEAR_ and GIS_ACRES fields),
            while the monthly chart shows acres burned by month for the selected year.
          </p>
          <div className="data-source-metadata">
            <div>
              <span className="data-source-label">Years Available:</span> 
              <span>{availableYears.join(', ')}</span>
            </div>
            <div>
              <span className="data-source-label">Worst Fire Year:</span> 
              <span className="worst-year">{summaryStats.worstYear} ({summaryStats.worstYearAcres.toLocaleString()} acres)</span>
            </div>
          </div>
        </div>
      </div>

      <div className="text-center">
        <button 
          onClick={fetchYearlyData} 
          className="refresh-button"
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="refresh-icon" viewBox="0 0 20 20" fill="currentColor">
            <path fillRule="evenodd" d="M4 2a1 1 0 011 1v2.101a7.002 7.002 0 0111.601 2.566 1 1 0 11-1.885.666A5.002 5.002 0 005.999 7H9a1 1 0 010 2H4a1 1 0 01-1-1V3a1 1 0 011-1zm.008 9.057a1 1 0 011.276.61A5.002 5.002 0 0014.001 13H11a1 1 0 110-2h5a1 1 0 011 1v5a1 1 0 11-2 0v-2.101a7.002 7.002 0 01-11.601-2.566 1 1 0 01.61-1.276z" clipRule="evenodd" />
          </svg>
          Refresh Data
        </button>
      </div>
    </div>
  );
};

export default CaliforniaFireDashboard;
