// Dashboard.jsx - Enhanced with base layer and overlay support
import React, { useState, useEffect, useRef } from 'react';
import * as d3 from 'd3';
import LayerControls from './LayerControls';
import './Dashboard.css';

const Dashboard = () => {
  // Base dataset state
  const [baseData, setBaseData] = useState(null);
  const [selectedBaseDataset, setSelectedBaseDataset] = useState('default');

  // Overlay datasets state
  const [overlays, setOverlays] = useState([]);
  const [availableOverlays, setAvailableOverlays] = useState([]);

  // UI state
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [availableDatasets, setAvailableDatasets] = useState([
    { id: 'default', name: 'Default Dataset' }
  ]);
  const [renderMethod, setRenderMethod] = useState('standard'); // 'standard' or 'simplified'

  // Refs for visualization containers
  const mapRef = useRef(null);
  const chartRef = useRef(null);
  const backendBaseUrl = 'http://localhost:8000';

  // Fetch available datasets on component mount
  useEffect(() => {
    const fetchDatasets = async () => {
      try {
        const response = await fetch(`${backendBaseUrl}/api/datasets`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const datasets = await response.json();
        setAvailableDatasets(datasets);

        // Set available overlays (all datasets except the currently selected base)
        updateAvailableOverlays(datasets, selectedBaseDataset);
      } catch (err) {
        console.error('Error fetching datasets:', err);
      }
    };

    fetchDatasets();
  }, []);

  // Update available overlays when base dataset changes
  const updateAvailableOverlays = (datasets, baseId) => {
    const filteredOverlays = datasets.filter(dataset => dataset.id !== baseId);
    setAvailableOverlays(filteredOverlays);
  };

  // Fetch base dataset when selection changes
  useEffect(() => {
    fetchBaseData(selectedBaseDataset);

    // Update available overlays when base dataset changes
    updateAvailableOverlays(availableDatasets, selectedBaseDataset);

    // Clear current overlays when base dataset changes
    setOverlays([]);
  }, [selectedBaseDataset]);

  // Fetch base dataset data
  const fetchBaseData = async (datasetId) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`${backendBaseUrl}/api/data/${datasetId}`);
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const result = await response.json();
      setBaseData(result);
      setLoading(false);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  // Toggle an overlay dataset
  const toggleOverlay = async (datasetId) => {
    // Check if the overlay is already added
    const existingOverlayIndex = overlays.findIndex(o => o.id === datasetId);

    if (existingOverlayIndex >= 0) {
      // Remove the overlay if it's already added
      setOverlays(overlays.filter(o => o.id !== datasetId));
    } else {
      // Add the overlay
      try {
        setLoading(true);
        const response = await fetch(`${backendBaseUrl}/api/data/${datasetId}`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const result = await response.json();

        // Add to overlays with default settings
        setOverlays([...overlays, {
          id: datasetId,
          data: result,
          visible: true,
          opacity: 0.7,
          colorScale: 'viridis', // Default color scale
          zIndex: overlays.length // Set z-index based on current overlay count
        }]);
        setLoading(false);
      } catch (err) {
        setError(`Error loading overlay: ${err.message}`);
        setLoading(false);
      }
    }
  };

  // Analyze layers (spatial operations between base and overlay)
  const analyzeLayer = async (overlayId, operation) => {
    try {
      setLoading(true);

      const response = await fetch(`${backendBaseUrl}/api/layers/analyze`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          baseLayerId: selectedBaseDataset,
          overlayLayerId: overlayId,
          operation
        })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const result = await response.json();

      if (result.success) {
        // Fetch the new dataset
        await fetchBaseData(result.datasetId);
        // Update the available datasets list
        const datasetsResponse = await fetch(`${backendBaseUrl}/api/datasets`);
        if (datasetsResponse.ok) {
          const datasets = await datasetsResponse.json();
          setAvailableDatasets(datasets);

          // Set the new analysis dataset as the base dataset
          setSelectedBaseDataset(result.datasetId);
        }
        setLoading(false);
      } else {
        throw new Error(result.error || 'Analysis operation failed');
      }
    } catch (err) {
      setError(`Error performing analysis: ${err.message}`);
      setLoading(false);
    }
  };

  // Update overlay settings
  const updateOverlay = (datasetId, updates) => {
    setOverlays(overlays.map(overlay => 
      overlay.id === datasetId ? { ...overlay, ...updates } : overlay
    ));
  };

  // Draw the base map and overlays
  const drawMap = () => {
    if (!baseData || !baseData.geoData || !mapRef.current) return;

    const mapContainer = d3.select(mapRef.current);
    mapContainer.selectAll("*").remove();

    // Check if we have valid GeoJSON data
    if (!baseData.geoData.features || baseData.geoData.features.length === 0) {
      mapContainer.append("div")
        .attr("class", "no-data-message")
        .text("No geographic data available to display");
      return;
    }

    const width = mapContainer.node().getBoundingClientRect().width;
    const height = 500;

    const svg = mapContainer
      .append("svg")
      .attr("width", width)
      .attr("height", height);

    // Add a base group for the base layer
    const baseGroup = svg.append("g").attr("class", "base-layer");

    // Add a tooltip div
    const tooltip = d3.select("body").append("div")
      .attr("class", "tooltip")
      .style("opacity", 0);

    try {
      // Clean the features to ensure valid coordinates
      const validFeatures = baseData.geoData.features.filter(feature => {
        if (!feature.geometry || !feature.geometry.coordinates) return false;

        // Complex check for valid coordinates omitted for brevity (same as in original)
        return true;
      });

      if (validFeatures.length === 0) {
        svg.append("text")
          .attr("x", width / 2)
          .attr("y", height / 2)
          .attr("text-anchor", "middle")
          .text("No valid geographic features found in the data");
        return;
      }

      const cleanedGeoData = {
        type: 'FeatureCollection',
        features: validFeatures
      };

      // Create a projection that works with the data
      const projection = d3.geoMercator()
        .fitSize([width, height], cleanedGeoData);

      const path = d3.geoPath().projection(projection);

      // Find a numeric property to use for coloring
      let valueProperty = null;
      if (cleanedGeoData.features.length > 0) {
        const properties = cleanedGeoData.features[0].properties;
        const numericProps = Object.keys(properties).filter(key => 
          typeof properties[key] === 'number' || 
          (typeof properties[key] === 'string' && !isNaN(parseFloat(properties[key])))
        );

        if (numericProps.length > 0) {
          valueProperty = numericProps[0];
        }
      }

      // Create a color scale for the base layer
      let colorScale = d3.scaleOrdinal(d3.schemeCategory10); // Default categorical scale

      if (valueProperty) {
        // Get min/max values for color scale
        const values = cleanedGeoData.features.map(f => {
          const val = f.properties[valueProperty];
          return typeof val === 'number' ? val : parseFloat(val);
        }).filter(val => !isNaN(val));

        const minValue = values.length > 0 ? Math.min(...values) : 0;
        const maxValue = values.length > 0 ? Math.max(...values) : 1;

        // Create a sequential color scale for numeric data
        colorScale = d3.scaleSequential()
          .domain([minValue, maxValue])
          .interpolator(d3.interpolateViridis);
      }

      // Draw base layer
      baseGroup.selectAll("path")
        .data(cleanedGeoData.features)
        .enter()
        .append("path")
        .attr("d", feature => {
          try {
            return path(feature);
          } catch (e) {
            console.warn("Error rendering feature:", e, feature);
            return "";
          }
        })
        .attr("fill", d => {
          if (valueProperty) {
            const val = d.properties[valueProperty];
            const numericVal = typeof val === 'number' ? val : parseFloat(val);
            return !isNaN(numericVal) ? colorScale(numericVal) : "#ccc";
          }
          return "#4682b4"; // Default color if no value property
        })
        .attr("stroke", "#fff")
        .attr("stroke-width", 0.5)
        .on("mouseover", function(event, d) {
          d3.select(this)
            .attr("stroke-width", 1.5)
            .attr("stroke", "#000");

          // Show tooltip with properties
          let tooltipHTML = `<div style="font-weight:bold;border-bottom:1px solid #ccc;padding-bottom:3px;margin-bottom:3px">
            ${d.properties.name || d.properties.NAME || valueProperty + ': ' + d.properties[valueProperty] || 'Feature'}</div>`;

          // Add all properties to tooltip
          Object.entries(d.properties).forEach(([key, value]) => {
            if (key !== 'name' && key !== 'NAME') {
              tooltipHTML += `<div><strong>${key}:</strong> ${value}</div>`;
            }
          });

          tooltip
            .style("left", (event.pageX + 10) + "px")
            .style("top", (event.pageY - 20) + "px")
            .style("opacity", 1)
            .html(tooltipHTML);
        })
        .on("mouseout", function() {
          d3.select(this)
            .attr("stroke-width", 0.5)
            .attr("stroke", "#fff");

          // Hide tooltip
          tooltip.style("opacity", 0);
        });

      // Add base layer legend if we have a value property
      if (valueProperty) {
        addLegend(svg, colorScale, valueProperty, width, height, 'base');
      }

      // Sort overlays by z-index before drawing
      const sortedOverlays = [...overlays].sort((a, b) => (a.zIndex || 0) - (b.zIndex || 0));

      // Draw each overlay
      sortedOverlays.forEach((overlay, index) => {
        if (!overlay.visible || !overlay.data || !overlay.data.geoData) return;

        // Add a group for this overlay
        const overlayGroup = svg.append("g")
          .attr("class", `overlay-layer-${overlay.id}`)
          .style("opacity", overlay.opacity);

        // Process overlay features
        const overlayFeatures = overlay.data.geoData.features.filter(feature => {
          return feature.geometry && feature.geometry.coordinates;
        });

        if (overlayFeatures.length === 0) return;

        const overlayGeoData = {
          type: 'FeatureCollection',
          features: overlayFeatures
        };

        // Find a value property for this overlay
        let overlayValueProperty = null;
        if (overlayFeatures.length > 0) {
          const properties = overlayFeatures[0].properties;
          const numericProps = Object.keys(properties).filter(key => 
            typeof properties[key] === 'number' || 
            (typeof properties[key] === 'string' && !isNaN(parseFloat(properties[key])))
          );

          if (numericProps.length > 0) {
            overlayValueProperty = numericProps[0];
          }
        }

        // Create a color scale for this overlay
        let overlayColorScale;

        if (overlayValueProperty) {
          // Get min/max values for color scale
          const values = overlayFeatures.map(f => {
            const val = f.properties[overlayValueProperty];
            return typeof val === 'number' ? val : parseFloat(val);
          }).filter(val => !isNaN(val));

          const minValue = values.length > 0 ? Math.min(...values) : 0;
          const maxValue = values.length > 0 ? Math.max(...values) : 1;

          // Check the color scale type
          const colorScheme = overlay.colorScale || 'viridis';

          if (colorScheme === 'category10' || colorScheme === 'paired' || colorScheme === 'set3') {
            // Categorical scales
            if (colorScheme === 'category10') {
              overlayColorScale = d3.scaleOrdinal(d3.schemeCategory10);
            } else if (colorScheme === 'paired') {
              overlayColorScale = d3.scaleOrdinal(d3.schemePaired);
            } else {
              overlayColorScale = d3.scaleOrdinal(d3.schemeSet3);
            }
          } else if (colorScheme === 'spectral' || colorScheme === 'rdylbu') {
            // Diverging scales
            overlayColorScale = d3.scaleSequential()
              .domain([minValue, maxValue])
              .interpolator(colorScheme === 'spectral' ? d3.interpolateSpectral : d3.interpolateRdYlBu);
          } else {
            // Sequential scales
            const colorInterpolator = getColorInterpolator(colorScheme);
            overlayColorScale = d3.scaleSequential()
              .domain([minValue, maxValue])
              .interpolator(colorInterpolator);
          }
        } else {
          // Default categorical scale if no numeric property
          overlayColorScale = d3.scaleOrdinal(d3.schemeSet2);
        }

        // Draw overlay features
        overlayGroup.selectAll("path")
          .data(overlayFeatures)
          .enter()
          .append("path")
          .attr("d", feature => {
            try {
              return path(feature);
            } catch (e) {
              console.warn("Error rendering overlay feature:", e, feature);
              return "";
            }
          })
          .attr("fill", d => {
            if (overlayValueProperty) {
              const val = d.properties[overlayValueProperty];
              const numericVal = typeof val === 'number' ? val : parseFloat(val);
              return !isNaN(numericVal) ? overlayColorScale(numericVal) : "#ccc";
            }
            return "#ff7f0e"; // Default overlay color
          })
          .attr("stroke", "#fff")
          .attr("stroke-width", 0.5)
          .on("mouseover", function(event, d) {
            d3.select(this)
              .attr("stroke-width", 1.5)
              .attr("stroke", "#000");

            // Show tooltip with properties
            let tooltipHTML = `<div style="font-weight:bold;border-bottom:1px solid #ccc;padding-bottom:3px;margin-bottom:3px">
              Overlay: ${d.properties.name || d.properties.NAME || overlayValueProperty + ': ' + d.properties[overlayValueProperty] || 'Feature'}</div>`;

            // Add all properties to tooltip
            Object.entries(d.properties).forEach(([key, value]) => {
              if (key !== 'name' && key !== 'NAME') {
                tooltipHTML += `<div><strong>${key}:</strong> ${value}</div>`;
              }
            });

            tooltip
              .style("left", (event.pageX + 10) + "px")
              .style("top", (event.pageY - 20) + "px")
              .style("opacity", 1)
              .html(tooltipHTML);
          })
          .on("mouseout", function() {
            d3.select(this)
              .attr("stroke-width", 0.5)
              .attr("stroke", "#fff");

            // Hide tooltip
            tooltip.style("opacity", 0);
          });

        // Add legend for this overlay if it has a value property
        if (overlayValueProperty) {
          addLegend(svg, overlayColorScale, overlayValueProperty, width, height, `overlay-${index}`, 
                   index + 1); // Position legends with offset
        }
      });
    } catch (err) {
      console.error('Error rendering map:', err);
      svg.append("text")
        .attr("x", width / 2)
        .attr("y", height / 2)
        .attr("text-anchor", "middle")
        .text("Error rendering map. See console for details.");
    }
  };

  // Helper to get a color interpolator based on name
  const getColorInterpolator = (name) => {
    const interpolators = {
      'viridis': d3.interpolateViridis,
      'inferno': d3.interpolateInferno,
      'plasma': d3.interpolatePlasma,
      'magma': d3.interpolateMagma,
      'blues': d3.interpolateBlues,
      'greens': d3.interpolateGreens,
      'reds': d3.interpolateReds,
      'greys': d3.interpolateGreys,
      'purples': d3.interpolatePurples,
      'oranges': d3.interpolateOranges
    };

    return interpolators[name] || d3.interpolateViridis;
  };

  // Helper to add a legend to the map
  const addLegend = (svg, colorScale, propertyName, width, height, id, offsetMultiplier = 0) => {
    const legendWidth = 20;
    const legendHeight = 150;
    const legendMargin = 30;
    const legendX = width - legendWidth - 20 - (offsetMultiplier * (legendWidth + 20));
    const legendY = (height - legendHeight) / 2;

    const legend = svg.append("g")
      .attr("class", `legend-${id}`)
      .attr("transform", `translate(${legendX}, ${legendY})`);

    // Only add continuous legend for sequential scales
    if (typeof colorScale.interpolator === 'function') {
      const legendScale = d3.scaleLinear()
        .domain(colorScale.domain())
        .range([legendHeight, 0]);

      const legendAxis = d3.axisRight(legendScale)
        .tickSize(legendWidth)
        .ticks(5);

      legend.append("g")
        .call(legendAxis);

      const defs = svg.append("defs");
      const linearGradient = defs.append("linearGradient")
        .attr("id", `linear-gradient-${id}`)
        .attr("x1", "0%")
        .attr("y1", "100%")
        .attr("x2", "0%")
        .attr("y2", "0%");

      const stops = [0, 0.2, 0.4, 0.6, 0.8, 1];
      stops.forEach(stop => {
        linearGradient.append("stop")
          .attr("offset", `${stop * 100}%`)
          .attr("stop-color", colorScale(colorScale.domain()[0] + stop * (colorScale.domain()[1] - colorScale.domain()[0])));
      });

      legend.append("rect")
        .attr("width", legendWidth)
        .attr("height", legendHeight)
        .style("fill", `url(#linear-gradient-${id})`);
    }

    // Add legend title
    legend.append("text")
      .attr("x", legendWidth / 2)
      .attr("y", -10)
      .attr("text-anchor", "middle")
      .style("font-size", "12px")
      .text(propertyName);

    // Add label to indicate which dataset this is
    legend.append("text")
      .attr("x", legendWidth / 2)
      .attr("y", legendHeight + 20)
      .attr("text-anchor", "middle")
      .style("font-size", "10px")
      .style("font-style", "italic")
      .text(id === 'base' ? 'Base layer' : `Overlay ${offsetMultiplier}`);
  };

  // Use effect to redraw map when base data or overlays change
  useEffect(() => {
    if (baseData && !loading) {
      try {
        drawMap();
      } catch (e) {
        console.error("Error rendering map:", e);
        setError("Error rendering map");
      }
    }
  }, [baseData, overlays, loading, renderMethod]);

  // Draw chart based on base data
  const drawChart = () => {
    if (!baseData || !baseData.csvData || !chartRef.current) return;

    // Chart drawing code (similar to original)
    const chartContainer = d3.select(chartRef.current);
    chartContainer.selectAll("*").remove();

    if (!baseData.csvData || baseData.csvData.length === 0) {
      chartContainer.append("div")
        .attr("class", "no-data-message")
        .text("No chart data available to display");
      return;
    }

    // Rest of the chart drawing code (same as original)
    // ...
  };

  // Use effect to redraw chart when base data changes
  useEffect(() => {
    if (baseData && !loading) {
      try {
        drawChart();
      } catch (e) {
        console.error("Error rendering chart:", e);
      }
    }
  }, [baseData, loading]);

  // Render the component
  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>Data Visualization Dashboard</h1>
        <div className="controls">
          <label htmlFor="dataset-select">Base Dataset:</label>
          <select 
            id="dataset-select" 
            value={selectedBaseDataset}
            onChange={(e) => setSelectedBaseDataset(e.target.value)}
          >
            {availableDatasets.map(dataset => (
              <option key={dataset.id} value={dataset.id}>
                {dataset.name}
              </option>
            ))}
          </select>
        </div>
      </header>

      {loading && <div className="loading">Loading data...</div>}
      {error && <div className="error">Error: {error}</div>}

      {baseData && !loading && (
        <div className="dashboard-content">
          {/* Overlay controls */}
          <div className="widget overlay-controls">
            <h2>Overlay Controls</h2>
            <div className="overlay-list">
              {availableOverlays.map(dataset => {
                const isActive = overlays.some(o => o.id === dataset.id);
                const activeOverlay = overlays.find(o => o.id === dataset.id);

                return (
                  <div key={dataset.id} className="overlay-item">
                    <div className="overlay-header">
                      <label className="overlay-toggle">
                        <input 
                          type="checkbox" 
                          checked={isActive}
                          onChange={() => toggleOverlay(dataset.id)}
                        />
                        {dataset.name}
                      </label>
                    </div>

                    {isActive && activeOverlay && (
                      <div className="overlay-settings">
                        <div className="setting">
                          <label>Visible:</label>
                          <input 
                            type="checkbox"
                            checked={activeOverlay.visible}
                            onChange={(e) => updateOverlay(dataset.id, { visible: e.target.checked })}
                          />
                        </div>

                        <div className="setting">
                          <label>Opacity: {activeOverlay.opacity}</label>
                          <input 
                            type="range"
                            min="0.1"
                            max="1"
                            step="0.1"
                            value={activeOverlay.opacity}
                            onChange={(e) => updateOverlay(dataset.id, { opacity: parseFloat(e.target.value) })}
                          />
                        </div>

                        <div className="setting">
                          <label>Color Scheme:</label>
                          <select
                            value={activeOverlay.colorScale}
                            onChange={(e) => updateOverlay(dataset.id, { colorScale: e.target.value })}
                          >
                            <option value="viridis">Viridis</option>
                            <option value="inferno">Inferno</option>
                            <option value="plasma">Plasma</option>
                            <option value="magma">Magma</option>
                            <option value="blues">Blues</option>
                            <option value="greens">Greens</option>
                            <option value="reds">Reds</option>
                            <option value="purples">Purples</option>
                            <option value="oranges">Oranges</option>
                          </select>
                        </div>
                      </div>
                    )}
                  </div>
                );
              })}
            </div>
          </div>

          <div className="widget full-width">
            <h2>Geographic Data Visualization</h2>
            <div className="map-container" ref={mapRef}></div>
          </div>

          <div className="widget-row">
            <div className="widget">
              <h2>Base Data Trends</h2>
              <div className="chart-container" ref={chartRef}></div>
            </div>

            <div className="widget">
              <h2>Base Data Metrics</h2>
              <div className="metrics-container">
                <div className="metric-card">
                  <h3>Total</h3>
                  <p className="metric-value">{baseData.metrics?.total?.toLocaleString() || 'N/A'}</p>
                </div>
                <div className="metric-card">
                  <h3>Average</h3>
                  <p className="metric-value">{baseData.metrics?.average?.toFixed(2) || 'N/A'}</p>
                </div>
                <div className="metric-card">
                  <h3>Maximum</h3>
                  <p className="metric-value">{baseData.metrics?.maximum?.toLocaleString() || 'N/A'}</p>
                </div>
                <div className="metric-card">
                  <h3>Minimum</h3>
                  <p className="metric-value">{baseData.metrics?.minimum?.toLocaleString() || 'N/A'}</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      <div className="tooltip" style={{ display: 'none' }}></div>
    </div>
  );
};

export default Dashboard;
